  const [count, setCount] = useState(0);
  const [title, setTitle] = useState("sf");
  const handleClickCount = () => {
    setCount(c => {
      return c + 1;
    });
  };

  const handleClick = () => {
    console.log("点击", count);
  };

  const handleChange = () => {
    console.log("input改变了", count);
  };
  const handleMemo = useMemo(() => {
    return {
      handleClick,
      handleChange
    };
  }, []);

  return (
    <div>
      <h1>{count}</h1>
      <button onClick={handleClickCount}>click </button>
      {/* <Container handleClick={handleClick} handleChange={handleChange}  title={title}></Container> */}
      <Container {...handleMemo} title={title}></Container>
    </div>
  );
};

export default App;
