//Context ‰∏ªË¶ÅÊòØËß£ÂÜ≥propsÂêëÂ§öÂ±ÇÂµåÂ•óÁöÑÂ≠êÁªÑ‰ª∂‰º†ÈÄíÁöÑÈóÆÈ¢òÔºåÂéüÁêÜÊòØÂÆö‰πâ‰∫Ü‰∏Ä‰∏™ÂÖ®Â±ÄÂØπË±°
import React from "react";
import PropTypes from "prop-types";

const { Provider, Consumer } = React.createContext("default");

class Parent extends React.Component {
  state = {
    yideng: "ÊôÆÈÄöÂ≠óÁ¨¶‰∏≤üçå",
    newContext: "‰∫¨Á®ã‰∏ÄÁÅØ"
  };

  //   getChildContext() {
  //     return { value: this.state.newContext, yideng: this.state.yideng };
  //   }
  render() {
    //    <React.Fragment> ==  <>
    return (
      <>
        <div>
          <label className="text-warning">Áà∂ËäÇÁÇπ=> newContext:</label>
          <input
            type="text"
            value={this.state.newContext}
            onChange={e => this.setState({ newContext: e.target.value })}
          />
        </div>
        <div>
          <label className="text-info">Áà∂ËäÇÁÇπ=>yideng:</label>
          <input
            type="text"
            value={this.state.yideng}
            onChange={e => this.setState({ yideng: e.target.value })}
          />
        </div>
        {/* {this.props.children} */}
        <Provider
          value={{ newContext: this.state.newContext, yideng: "ÊôÆÈÄöÂ≠óÁ¨¶‰∏≤üçå" }}
        >
          {this.props.children}
        </Provider>
      </>
    );
  }
}

function Child(props, context) {
  return (
    <Consumer>
      {value => (
        <p className="text-warning">Â≠êËäÇÁÇπ=> newContext: {value.newContext}</p>
      )}
    </Consumer>
  );
}

class Child2 extends React.Component {
  static contextTypes = {
    yideng: PropTypes.string
  };
  render() {
    // return <p>Â≠óÁ¨¶‰∏≤a: {this.context.yideng}</p>;
    return (
      <Consumer>
        {value => <p className="text-info">Â≠êËäÇÁÇπ=> yideng: {value.yideng}</p>}
      </Consumer>
    );
  }
}
Child.contextTypes = {
  value: PropTypes.string
};
// Parent.childContextTypes = {
//   value: PropTypes.string,
//   yideng: PropTypes.string
// };

export default () => (
  <Parent>
    <Child />
    <Child2 />
  </Parent>
);
