import React, { Component } from "react";
class Greeting extends Component {
  constructor(props) {
    super(props);
    this.state = { count: props.initialCount };
  }
  static defaultProps = {
    name: "普通Component组件"
  };
  render() {
     return <h4 className="text-warning">Hello, {this.props.name}</h4>;
